import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertSolarWindReadingSchema, insertMappingConfigSchema, insertHardwareConfigSchema, insertAmbientSettingsSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {

  // NOAA Data Fetching and Solar Wind API
  app.get("/api/solar-wind/current", async (req, res) => {
    try {
      const reading = await storage.getLatestSolarWindReading();
      if (!reading) {
        return res.status(404).json({ message: "No solar wind data available" });
      }
      res.json(reading);
    } catch (error) {
      console.error("Error fetching current solar wind data:", error);
      res.status(500).json({ message: "Failed to fetch solar wind data" });
    }
  });

  app.get("/api/solar-wind/history", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 100;
      const readings = await storage.getSolarWindReadings(limit);
      res.json(readings);
    } catch (error) {
      console.error("Error fetching solar wind history:", error);
      res.status(500).json({ message: "Failed to fetch solar wind history" });
    }
  });

  // Fetch real-time data from NOAA DSCOVR
  app.post("/api/solar-wind/fetch", async (req, res) => {
    try {
      // NOAA SWPC real-time plasma data endpoint (updated URL)
      const noaaResponse = await fetch('https://services.swpc.noaa.gov/products/solar-wind/plasma-2-hour.json');

      if (!noaaResponse.ok) {
        throw new Error(`NOAA API error: ${noaaResponse.status}`);
      }

      const noaaData = await noaaResponse.json();

      // Parse NOAA data format - skip header row, take latest entry
      const dataRows = noaaData.slice(1); // Remove header row
      const latestData = dataRows[dataRows.length - 1];

      if (!latestData || latestData.length < 4) {
        throw new Error(`Invalid NOAA data format. Expected array with 4+ elements, got: ${JSON.stringify(latestData)}`);
      }

      // New NOAA format: [time_tag, density, speed, temperature]
      // Note: Bz data needs to be fetched from magnetometer endpoint separately
      const solarWindData = {
        velocity: parseFloat(latestData[2]) || 0, // speed km/s
        density: parseFloat(latestData[1]) || 0, // proton density p/cm³
        bz: 0, // Will fetch from mag endpoint later
        bt: null, // Not available in plasma endpoint
        temperature: parseFloat(latestData[3]) || 0, // temperature K
        raw_data: latestData
      };

      const reading = await storage.createSolarWindReading(solarWindData);

      // Update system status
      await storage.updateSystemStatus({
        component: 'data_stream',
        status: 'active',
        details: `Updated: ${new Date().toISOString()}`
      });

      res.json(reading);
    } catch (error) {
      console.error("Error fetching NOAA data:", error);

      const errorMessage = error instanceof Error ? error.message : 'Unknown error';

      // Update system status to error
      await storage.updateSystemStatus({
        component: 'data_stream',
        status: 'error',
        details: `NOAA fetch failed: ${errorMessage}`
      });

      res.status(500).json({ message: "Failed to fetch NOAA data", error: errorMessage });
    }
  });

  // MIDI Mapping Configuration
  app.get("/api/mapping/configs", async (req, res) => {
    try {
      const configs = await storage.getMappingConfigs();
      res.json(configs);
    } catch (error) {
      console.error("Error fetching mapping configs:", error);
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(500).json({ message: "Failed to fetch mapping configurations", error: errorMessage });
    }
  });

  app.get("/api/mapping/active", async (req, res) => {
    try {
      const config = await storage.getActiveMappingConfig();
      if (!config) {
        return res.status(404).json({ message: "No active mapping configuration found" });
      }
      res.json(config);
    } catch (error) {
      console.error("Error fetching active mapping config:", error);
      res.status(500).json({ message: "Failed to fetch active mapping configuration" });
    }
  });

  app.post("/api/mapping/configs", async (req, res) => {
    try {
      const validatedData = insertMappingConfigSchema.parse(req.body);
      const config = await storage.createMappingConfig(validatedData);
      res.status(201).json(config);
    } catch (error) {
      console.error("Error creating mapping config:", error);
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(400).json({ message: "Invalid mapping configuration data", error: errorMessage });
    }
  });

  app.patch("/api/mapping/configs/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;
      const config = await storage.updateMappingConfig(id, updates);

      if (!config) {
        return res.status(404).json({ message: "Mapping configuration not found" });
      }

      res.json(config);
    } catch (error) {
      console.error("Error updating mapping config:", error);
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(400).json({ message: "Failed to update mapping configuration", error: errorMessage });
    }
  });

  // Solar Wind to MIDI/Chord Mapping
  app.post("/api/mapping/calculate-chord", async (req, res) => {
    try {
      const { velocity, density, bz } = req.body;

      if (typeof velocity !== 'number' || typeof density !== 'number' || typeof bz !== 'number') {
        return res.status(400).json({ message: "Invalid input parameters" });
      }

      const mappingConfig = await storage.getActiveMappingConfig();
      if (!mappingConfig) {
        return res.status(404).json({ message: "No active mapping configuration" });
      }

      // Calculate MIDI note from velocity (200-800 km/s → C2-C6)
      const velocityRange = mappingConfig.velocity_max - mappingConfig.velocity_min;
      const midiRange = mappingConfig.midi_note_max - mappingConfig.midi_note_min;
      const velocityNormalized = Math.max(0, Math.min(1, (velocity - mappingConfig.velocity_min) / velocityRange));
      const midiNote = Math.round(mappingConfig.midi_note_min + (velocityNormalized * midiRange));

      // Convert MIDI note to frequency (A440 standard)
      const frequency = 440 * Math.pow(2, (midiNote - 69) / 12);

      // Calculate note name
      const noteNames = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'A#', 'B'];
      const octave = Math.floor((midiNote - 12) / 12);
      const noteIndex = (midiNote - 12) % 12;
      const noteName = `${noteNames[noteIndex]}${octave}`;

      // Calculate decay time from density (logarithmic mapping)
      const densityNormalized = Math.max(0, Math.min(1, Math.log(density / mappingConfig.density_min) / Math.log(mappingConfig.density_max / mappingConfig.density_min)));
      const decayTime = mappingConfig.decay_time_min + ((1 - densityNormalized) * (mappingConfig.decay_time_max - mappingConfig.decay_time_min));

      // Calculate detune from Bz (southward creates beating)
      const detuneCents = bz < mappingConfig.bz_threshold ? mappingConfig.bz_detune_cents : 0;

      // Determine space weather condition
      let condition: 'quiet' | 'moderate' | 'storm' = 'quiet';
      if (velocity > 600 || Math.abs(bz) > 10) {
        condition = 'storm';
      } else if (velocity > 450 || Math.abs(bz) > 5) {
        condition = 'moderate';
      }

      const chordData = {
        baseNote: noteName,
        frequency,
        midiNote,
        decayTime: Math.round(decayTime * 100) / 100,
        detuneCents,
        condition,
        velocity,
        density,
        bz
      };

      res.json(chordData);
    } catch (error) {
      console.error("Error calculating chord:", error);
      res.status(500).json({ message: "Failed to calculate chord mapping" });
    }
  });

  // Test different space weather conditions
  app.post("/api/mapping/test-condition", async (req, res) => {
    try {
      const { condition } = req.body;

      let testData;
      switch (condition) {
        case 'quiet':
          testData = { velocity: 350, density: 5.0, bz: 2.0 };
          break;
        case 'moderate':
          testData = { velocity: 500, density: 8.0, bz: -7.0 };
          break;
        case 'storm':
          testData = { velocity: 750, density: 2.0, bz: -15.0 };
          break;
        default:
          return res.status(400).json({ message: "Invalid condition. Use 'quiet', 'moderate', or 'storm'" });
      }

      // Calculate chord for test condition
      const chordResponse = await fetch(`${req.protocol}://${req.get('host')}/api/mapping/calculate-chord`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(testData)
      });

      const chordData = await chordResponse.json();
      res.json({ condition, testData, chord: chordData });
    } catch (error) {
      console.error("Error testing condition:", error);
      res.status(500).json({ message: "Failed to test space weather condition" });
    }
  });

  // Hardware Configuration
  app.get("/api/hardware/configs", async (req, res) => {
    try {
      const configs = await storage.getHardwareConfigs();
      res.json(configs);
    } catch (error) {
      console.error("Error fetching hardware configs:", error);
      res.status(500).json({ message: "Failed to fetch hardware configurations" });
    }
  });

  app.post("/api/hardware/configs", async (req, res) => {
    try {
      const validatedData = insertHardwareConfigSchema.parse(req.body);
      const config = await storage.createHardwareConfig(validatedData);
      res.status(201).json(config);
    } catch (error) {
      console.error("Error creating hardware config:", error);
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(400).json({ message: "Invalid hardware configuration data", error: errorMessage });
    }
  });

  // ESP32 Firmware Generation
  app.post("/api/hardware/generate-firmware", async (req, res) => {
    try {
      const { configId } = req.body;

      const config = await storage.getHardwareConfig(configId);
      if (!config) {
        return res.status(404).json({ message: "Hardware configuration not found" });
      }

      const mappingConfig = await storage.getActiveMappingConfig();
      if (!mappingConfig) {
        return res.status(404).json({ message: "No active mapping configuration" });
      }

      // Generate ESP32 firmware code based on device type
      const firmwareCode = config.device_name === "Solar Lighthouse Prototype"
        ? generateSolarLighthouseFirmware(config, mappingConfig)
        : generateESP32Firmware(config, mappingConfig);

      res.json({
        firmware_code: firmwareCode,
        config_id: configId,
        generated_at: new Date().toISOString()
      });
    } catch (error) {
      console.error("Error generating firmware:", error);
      res.status(500).json({ message: "Failed to generate firmware code" });
    }
  });

  // System Status and Monitoring
  app.get("/api/system/status", async (req, res) => {
    try {
      const statuses = await storage.getSystemStatus();
      res.json(statuses);
    } catch (error) {
      console.error("Error fetching system status:", error);
      res.status(500).json({ message: "Failed to fetch system status" });
    }
  });

  app.post("/api/system/test-chimes", async (req, res) => {
    try {
      // Simulate chime testing
      await storage.updateSystemStatus({
        component: 'chimes',
        status: 'testing',
        details: 'Running chime test sequence'
      });

      // Simulate test delay
      setTimeout(async () => {
        await storage.updateSystemStatus({
          component: 'chimes',
          status: 'active',
          details: 'Chime test completed successfully'
        });
      }, 3000);

      res.json({ message: "Chime test sequence initiated" });
    } catch (error) {
      console.error("Error testing chimes:", error);
      res.status(500).json({ message: "Failed to test chimes" });
    }
  });

  // Ambient Mode Settings
  app.get("/api/settings/ambient", async (req, res) => {
    try {
      const settings = await storage.getAmbientSettings();
      if (!settings) {
        // Return default settings if none exist
        return res.json({
          enabled: "false",
          intensity: 0.5,
          volume: 0.3,
          respect_night: "true",
          day_only: "false",
          smoothing: 0.8,
          max_rate: 10.0,
          battery_min: 20.0
        });
      }
      res.json(settings);
    } catch (error) {
      console.error("Error fetching ambient settings:", error);
      res.status(500).json({ message: "Failed to fetch ambient settings" });
    }
  });

  app.post("/api/settings/ambient", async (req, res) => {
    try {
      const validatedData = insertAmbientSettingsSchema.parse(req.body);
      const settings = await storage.updateAmbientSettings(validatedData);

      // Update system status to reflect ambient mode state
      await storage.updateSystemStatus({
        component: 'chimes',
        status: settings.enabled === "true" ? 'ambient' : 'active',
        details: `Ambient mode ${settings.enabled === "true" ? 'enabled' : 'disabled'}: intensity=${settings.intensity}, volume=${settings.volume}`
      });

      res.json(settings);
    } catch (error) {
      console.error("Error updating ambient settings:", error);
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      res.status(400).json({ message: "Invalid ambient settings data", error: errorMessage });
    }
  });

  app.get("/api/mode", async (req, res) => {
    try {
      const ambientSettings = await storage.getAmbientSettings();
      const systemStatus = await storage.getSystemStatus();
      const latestSolarWind = await storage.getLatestSolarWindReading();

      res.json({
        ambient: {
          enabled: ambientSettings?.enabled === "true" || false,
          intensity: ambientSettings?.intensity || 0.5,
          volume: ambientSettings?.volume || 0.3
        },
        system: {
          data_stream: systemStatus.find(s => s.component === 'data_stream')?.status || 'unknown',
          chimes: systemStatus.find(s => s.component === 'chimes')?.status || 'unknown',
          power: systemStatus.find(s => s.component === 'power')?.status || 'unknown'
        },
        solar_wind: latestSolarWind ? {
          velocity: latestSolarWind.velocity,
          density: latestSolarWind.density,
          bz: latestSolarWind.bz,
          condition: latestSolarWind.velocity > 600 ? 'storm' : latestSolarWind.velocity > 450 ? 'moderate' : 'quiet'
        } : null
      });
    } catch (error) {
      console.error("Error fetching system mode:", error);
      res.status(500).json({ message: "Failed to fetch system mode" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

// ESP32 Firmware Generator
function generateESP32Firmware(hardwareConfig: any, mappingConfig: any): string {
  const currentDate = new Date().toISOString().split('T')[0];

  return `/*
 * Solar Wind Chime ESP32 Firmware
 * Generated: ${currentDate}
 * Device: ${hardwareConfig.device_name}
 * Firmware Version: ${hardwareConfig.firmware_version}
 */

#include <WiFi.h>
#include <HTTPClient.h>
#include <ArduinoJson.h>

// Pin Configuration
#define PIN_VELOCITY_CHIME ${hardwareConfig.pin_velocity}
#define PIN_DENSITY_CHIME ${hardwareConfig.pin_density}
#define PIN_BZ_CHIME ${hardwareConfig.pin_bz}
#define PIN_STATUS_LED ${hardwareConfig.pin_status_led}
#define PIN_SDA ${hardwareConfig.pin_sda}
#define PIN_SCL ${hardwareConfig.pin_scl}

// WiFi Configuration
const char* ssid = "${hardwareConfig.wifi_ssid || 'YOUR_WIFI_SSID'}";
const char* password = "YOUR_WIFI_PASSWORD";

// API Configuration
const char* apiEndpoint = "https://services.swpc.noaa.gov/products/solar-wind/plasma-2-hour.json";
const unsigned long updateInterval = ${hardwareConfig.update_interval * 1000}; // milliseconds

// Mapping Configuration (Date-stamped: ${currentDate})
const float VELOCITY_MIN = ${mappingConfig.velocity_min};
const float VELOCITY_MAX = ${mappingConfig.velocity_max};
const int MIDI_NOTE_MIN = ${mappingConfig.midi_note_min};
const int MIDI_NOTE_MAX = ${mappingConfig.midi_note_max};
const float DENSITY_MIN = ${mappingConfig.density_min};
const float DENSITY_MAX = ${mappingConfig.density_max};
const float DECAY_TIME_MIN = ${mappingConfig.decay_time_min};
const float DECAY_TIME_MAX = ${mappingConfig.decay_time_max};
const int BZ_DETUNE_CENTS = ${mappingConfig.bz_detune_cents};
const float BZ_THRESHOLD = ${mappingConfig.bz_threshold};

// Global variables
unsigned long lastUpdate = 0;
float currentVelocity = 0;
float currentDensity = 0;
float currentBz = 0;

void setup() {
  Serial.begin(115200);

  // Initialize pins
  pinMode(PIN_VELOCITY_CHIME, OUTPUT);
  pinMode(PIN_DENSITY_CHIME, OUTPUT);
  pinMode(PIN_BZ_CHIME, OUTPUT);
  pinMode(PIN_STATUS_LED, OUTPUT);

  // Initialize WiFi
  WiFi.begin(ssid, password);
  while (WiFi.status() != WL_CONNECTED) {
    delay(1000);
    Serial.println("Connecting to WiFi...");
    digitalWrite(PIN_STATUS_LED, !digitalRead(PIN_STATUS_LED));
  }

  Serial.println("WiFi connected!");
  digitalWrite(PIN_STATUS_LED, HIGH);

  // Initial data fetch
  fetchSolarWindData();
}

void loop() {
  unsigned long currentTime = millis();

  if (currentTime - lastUpdate >= updateInterval) {
    fetchSolarWindData();
    lastUpdate = currentTime;
  }

  // Check for incoming serial commands for testing
  if (Serial.available()) {
    String command = Serial.readString();
    command.trim();

    if (command == "test") {
      testAllChimes();
    } else if (command == "status") {
      printStatus();
    }
  }

  delay(100);
}

void fetchSolarWindData() {
  if (WiFi.status() == WL_CONNECTED) {
    HTTPClient http;
    http.begin(apiEndpoint);

    int httpResponseCode = http.GET();

    if (httpResponseCode == 200) {
      String payload = http.getString();
      parseSolarWindData(payload);
      playChord();
      digitalWrite(PIN_STATUS_LED, HIGH);
    } else {
      Serial.printf("HTTP Error: %d\\n", httpResponseCode);
      digitalWrite(PIN_STATUS_LED, LOW);
    }

    http.end();
  }
}

void parseSolarWindData(String jsonData) {
  DynamicJsonDocument doc(8192);
  deserializeJson(doc, jsonData);

  if (doc.size() > 0) {
    JsonArray latestReading = doc[doc.size() - 1];

    if (latestReading.size() >= 8) {
      currentVelocity = latestReading[2]; // speed km/s
      currentDensity = latestReading[1];  // density p/cm³
      currentBz = latestReading[6];       // Bz GSM nT

      Serial.printf("Solar Wind Data - V: %.1f km/s, D: %.1f p/cm³, Bz: %.1f nT\\n", 
                   currentVelocity, currentDensity, currentBz);
    }
  }
}

void playChord() {
  // Calculate MIDI note from velocity
  float velocityNorm = constrain((currentVelocity - VELOCITY_MIN) / (VELOCITY_MAX - VELOCITY_MIN), 0.0, 1.0);
  int midiNote = MIDI_NOTE_MIN + (velocityNorm * (MIDI_NOTE_MAX - MIDI_NOTE_MIN));

  // Calculate decay time from density (logarithmic)
  float densityNorm = constrain(log(currentDensity / DENSITY_MIN) / log(DENSITY_MAX / DENSITY_MIN), 0.0, 1.0);
  float decayTime = DECAY_TIME_MIN + ((1.0 - densityNorm) * (DECAY_TIME_MAX - DECAY_TIME_MIN));

  // Check for Bz detuning
  bool shouldDetune = currentBz < BZ_THRESHOLD;

  // Play velocity chime (base note)
  int velocityDuration = map(midiNote, MIDI_NOTE_MIN, MIDI_NOTE_MAX, 50, 200);
  digitalWrite(PIN_VELOCITY_CHIME, HIGH);
  delay(velocityDuration);
  digitalWrite(PIN_VELOCITY_CHIME, LOW);

  delay(100);

  // Play density chime (envelope)
  int densityDuration = (int)(decayTime * 1000);
  digitalWrite(PIN_DENSITY_CHIME, HIGH);
  delay(densityDuration);
  digitalWrite(PIN_DENSITY_CHIME, LOW);

  delay(100);

  // Play Bz chime (detune indicator)
  if (shouldDetune) {
    // Create beating effect with rapid pulses
    for (int i = 0; i < 5; i++) {
      digitalWrite(PIN_BZ_CHIME, HIGH);
      delay(50);
      digitalWrite(PIN_BZ_CHIME, LOW);
      delay(50);
    }
  }

  Serial.printf("Chord played - MIDI: %d, Decay: %.2fs, Detune: %s\\n", 
               midiNote, decayTime, shouldDetune ? "Yes" : "No");
}

void testAllChimes() {
  Serial.println("Testing all chimes...");

  // Test each chime individually
  Serial.println("Testing velocity chime...");
  digitalWrite(PIN_VELOCITY_CHIME, HIGH);
  delay(200);
  digitalWrite(PIN_VELOCITY_CHIME, LOW);
  delay(500);

  Serial.println("Testing density chime...");
  digitalWrite(PIN_DENSITY_CHIME, HIGH);
  delay(200);
  digitalWrite(PIN_DENSITY_CHIME, LOW);
  delay(500);

  Serial.println("Testing Bz chime...");
  digitalWrite(PIN_BZ_CHIME, HIGH);
  delay(200);
  digitalWrite(PIN_BZ_CHIME, LOW);
  delay(500);

  Serial.println("Chime test complete!");
}

void printStatus() {
  Serial.println("=== Solar Wind Chime Status ===");
  Serial.printf("WiFi: %s\\n", WiFi.status() == WL_CONNECTED ? "Connected" : "Disconnected");
  Serial.printf("IP Address: %s\\n", WiFi.localIP().toString().c_str());
  Serial.printf("Current Data - V: %.1f km/s, D: %.1f p/cm³, Bz: %.1f nT\\n", 
               currentVelocity, currentDensity, currentBz);
  Serial.printf("Update Interval: %lu seconds\\n", updateInterval / 1000);
  Serial.printf("Firmware Version: ${hardwareConfig.firmware_version}\\n");
  Serial.println("===============================");
}
`;
}

// Solar Lighthouse Prototype Firmware Generation
function generateSolarLighthouseFirmware(hardwareConfig: any, mappingConfig: any): string {
  return `/*
 * Solar Lighthouse Prototype Firmware
 * Industrial-grade Solar Wind Chime with advanced power management
 * 
 * Hardware: ESP32-Pico-D4, 3x Magnetic Solenoids, Solar Charging
 * Features: Deep Sleep, Night Mode, Storm Party Override, OTA Updates
 * 
 * Acoustic Tuning:
 * - Chime 1 (Pin ${hardwareConfig.pin_velocity}): E3 - 164.8 Hz (235mm tube)
 * - Chime 2 (Pin ${hardwareConfig.pin_density}): A3 - 220.0 Hz (195mm tube)  
 * - Chime 3 (Pin ${hardwareConfig.pin_bz}): C#4 - 277.2 Hz (165mm tube)
 */

#include <WiFi.h>
#include <HTTPClient.h>
#include <ArduinoJson.h>
#include <esp_deep_sleep.h>
#include <esp_wifi.h>
#include <WiFiManager.h>
#include <ArduinoOTA.h>
#include <Wire.h>
#include <Adafruit_INA219.h>

// Hardware Configuration
#define PIN_CHIME_E3     ${hardwareConfig.pin_velocity}  // Solenoid 1: E3 (164.8 Hz)
#define PIN_CHIME_A3     ${hardwareConfig.pin_density}   // Solenoid 2: A3 (220.0 Hz)
#define PIN_CHIME_CS4    ${hardwareConfig.pin_bz}        // Solenoid 3: C#4 (277.2 Hz)
#define PIN_HEARTBEAT    ${hardwareConfig.pin_status_led} // Heartbeat LED
#define PIN_SDA          ${hardwareConfig.pin_sda}       // I2C Data
#define PIN_SCL          ${hardwareConfig.pin_scl}       // I2C Clock
#define PIN_MAST_TAP     27                              // Capacitive touch for storm-party mode
#define PIN_ALS          34                              // Ambient light sensor (ADC)

// Power Management
Adafruit_INA219 ina219; // Solar power monitor
#define BATTERY_MIN_VOLTAGE  2.3  // Min operating voltage
#define SOLAR_HARVEST_LOG    true // Log daily harvest via OTA

// Timing Configuration
#define DEEP_SLEEP_TIME     58000000ULL  // 58 seconds (microseconds)
#define UPDATE_INTERVAL     ${hardwareConfig.update_interval}000  // ms
#define SOLENOID_PULSE      50           // 50ms pulse duration
#define SOLENOID_CURRENT    600          // 600mA drive current

// Audio Parameters - Prototype Tuned
const float CHIME_FREQUENCIES[3] = {164.8, 220.0, 277.2}; // E3, A3, C#4 Hz
const int CHIME_PINS[3] = {PIN_CHIME_E3, PIN_CHIME_A3, PIN_CHIME_CS4};

// Space Weather Mapping
#define VELOCITY_MIN        ${mappingConfig.velocity_min}
#define VELOCITY_MAX        ${mappingConfig.velocity_max}
#define DENSITY_MIN         ${mappingConfig.density_min}
#define DENSITY_MAX         ${mappingConfig.density_max}
#define BZ_THRESHOLD        ${mappingConfig.bz_threshold}
#define BZ_DETUNE_CENTS     ${mappingConfig.bz_detune_cents}

// Night Mode & Storm Party
bool nightModeActive = false;
bool stormPartyMode = false;
unsigned long stormPartyEndTime = 0;
int lastTapCount = 0;
unsigned long lastTapTime = 0;
unsigned long lastNightStrike = 0;

// Solar Wind Data
float currentVelocity = 400.0;
float currentDensity = 5.0;
float currentBz = 0.0;
String spaceWeatherCondition = "quiet";

// API Configuration
const char* apiEndpoint = "https://services.swpc.noaa.gov/products/solar-wind/plasma-2-hour.json";
const char* firmwareVersion = "${hardwareConfig.firmware_version}";

void setup() {
  Serial.begin(115200);
  Serial.println("\\n=== Solar Lighthouse Prototype Starting ===");

  // Initialize hardware
  setupPins();
  setupPowerManagement();

  // WiFi and OTA setup
  setupWiFi();
  setupOTA();

  // Startup chime sequence
  playStartupChime();

  Serial.println("Solar Lighthouse ready for operation");
}

void loop() {
  // Check for storm party mode triple-tap
  checkStormPartyOverride();

  // Update night mode based on ambient light
  updateNightMode();

  // Fetch solar wind data and play chord
  if (WiFi.status() == WL_CONNECTED) {
    fetchSolarWindData();

    // Check if we should play (night mode restrictions)
    if (shouldPlayChime()) {
      calculateAndPlayChord();
    }

    // Log power telemetry
    logPowerTelemetry();
  }

  // Handle OTA updates
  ArduinoOTA.handle();

  // Update heartbeat LED
  updateHeartbeat();

  // Enter deep sleep for power conservation
  Serial.printf("Entering deep sleep for %d seconds\\n", ${hardwareConfig.update_interval});
  esp_deep_sleep(DEEP_SLEEP_TIME);
}

void setupPins() {
  // Solenoid outputs
  pinMode(PIN_CHIME_E3, OUTPUT);
  pinMode(PIN_CHIME_A3, OUTPUT);
  pinMode(PIN_CHIME_CS4, OUTPUT);

  // Status LED
  pinMode(PIN_HEARTBEAT, OUTPUT);

  // Touch sensor for storm party mode
  pinMode(PIN_MAST_TAP, INPUT);

  // Ambient light sensor
  pinMode(PIN_ALS, INPUT);

  // I2C for power monitoring
  Wire.begin(PIN_SDA, PIN_SCL);

  Serial.println("Hardware pins initialized");
}

void setupPowerManagement() {
  // Initialize INA219 power monitor
  if (ina219.begin()) {
    Serial.println("INA219 Power Monitor initialized");

    // Check battery voltage
    float voltage = ina219.getBusVoltage_V();
    if (voltage < BATTERY_MIN_VOLTAGE) {
      Serial.printf("WARNING: Low battery voltage: %.2fV\\n", voltage);
    }
  } else {
    Serial.println("Failed to initialize INA219");
  }
}

void setupWiFi() {
  WiFiManager wifiManager;

  // Auto-connect or start captive portal
  if (!wifiManager.autoConnect("SolarLighthouse")) {
    Serial.println("Failed to connect to WiFi");
    esp_restart();
  }

  Serial.printf("WiFi connected: %s\\n", WiFi.localIP().toString().c_str());
}

void setupOTA() {
  ArduinoOTA.setHostname("solar-lighthouse");
  ArduinoOTA.setPassword("space-weather");

  ArduinoOTA.onStart([]() {
    Serial.println("OTA Update starting...");
  });

  ArduinoOTA.onEnd([]() {
    Serial.println("\\nOTA Update complete");
  });

  ArduinoOTA.onError([](ota_error_t error) {
    Serial.printf("OTA Error[%u]: ", error);
  });

  ArduinoOTA.begin();
  Serial.println("OTA updates enabled");
}

void checkStormPartyOverride() {
  bool mastTapped = digitalRead(PIN_MAST_TAP);
  unsigned long currentTime = millis();

  if (mastTapped && (currentTime - lastTapTime) > 200) { // Debounce
    lastTapCount++;
    lastTapTime = currentTime;

    // Reset tap count after 2 seconds
    if ((currentTime - lastTapTime) > 2000) {
      lastTapCount = 0;
    }

    // Triple tap activates storm party mode
    if (lastTapCount >= 3) {
      stormPartyMode = true;
      stormPartyEndTime = currentTime + (2 * 60 * 60 * 1000); // 2 hours
      lastTapCount = 0;

      Serial.println("*** STORM PARTY MODE ACTIVATED ***");
      playStormPartyChime();
    }
  }

  // Check if storm party mode expired
  if (stormPartyMode && currentTime > stormPartyEndTime) {
    stormPartyMode = false;
    Serial.println("Storm party mode deactivated");
  }
}

void updateNightMode() {
  int lightLevel = analogRead(PIN_ALS);
  float lux = map(lightLevel, 0, 4095, 0, 1000); // Convert ADC to approximate lux

  nightModeActive = (lux < 10.0) && !stormPartyMode;

  static bool lastNightMode = false;
  if (nightModeActive != lastNightMode) {
    Serial.printf("Night mode: %s (%.1f lux)\\n", 
                 nightModeActive ? "ACTIVE" : "INACTIVE", lux);
    lastNightMode = nightModeActive;
  }
}

bool shouldPlayChime() {
  unsigned long currentTime = millis();

  // Storm party mode - always play
  if (stormPartyMode) return true;

  // Night mode restrictions
  if (nightModeActive) {
    // Limit to 1 strike every 5 minutes
    if ((currentTime - lastNightStrike) < (5 * 60 * 1000)) {
      return false;
    }
    lastNightStrike = currentTime;
  }

  return true;
}

void fetchSolarWindData() {
  HTTPClient http;
  http.begin(apiEndpoint);

  int httpResponseCode = http.GET();

  if (httpResponseCode == 200) {
    String payload = http.getString();
    parseSolarWindData(payload);
    Serial.printf("Solar Wind: %.1f km/s, %.1f p/cm³, %.1f nT\\n", 
                 currentVelocity, currentDensity, currentBz);
  } else {
    Serial.printf("HTTP Error: %d\\n", httpResponseCode);
  }

  http.end();
}

void parseSolarWindData(String jsonData) {
  DynamicJsonDocument doc(8192);
  deserializeJson(doc, jsonData);

  if (doc.size() > 1) {
    JsonArray latestReading = doc[doc.size() - 1];

    if (latestReading.size() >= 4) {
      currentVelocity = latestReading[2].as<float>(); // speed km/s
      currentDensity = latestReading[1].as<float>();  // density p/cm³
      currentBz = 0.0; // Placeholder - fetch from magnetometer endpoint

      // Classify space weather condition
      if (currentVelocity > 700 || currentBz < -15) {
        spaceWeatherCondition = "extreme";
      } else if (currentVelocity > 600 || currentBz < -10) {
        spaceWeatherCondition = "storm";
      } else if (currentVelocity > 450 || currentBz < -5) {
        spaceWeatherCondition = "moderate";
      } else {
        spaceWeatherCondition = "quiet";
      }
    }
  }
}

void calculateAndPlayChord() {
  // Map velocity to primary chime selection (E3, A3, or C#4)
  float velocityNorm = constrain((currentVelocity - VELOCITY_MIN) / (VELOCITY_MAX - VELOCITY_MIN), 0.0, 1.0);
  int primaryChime = (int)(velocityNorm * 2.99); // 0, 1, or 2

  // Calculate strike intensity from density (logarithmic)
  float densityNorm = constrain(log(currentDensity / DENSITY_MIN) / log(DENSITY_MAX / DENSITY_MIN), 0.0, 1.0);
  int strikeDuration = 20 + (densityNorm * 80); // 20-100ms

  // Detune effect for negative Bz (geomagnetic activity)
  bool shouldDetune = currentBz < BZ_THRESHOLD;

  // Apply night mode volume reduction
  float volumeMultiplier = nightModeActive ? 0.32 : 1.0; // -15 dB = 0.32x

  Serial.printf("Playing chord: Chime %d (%.1f Hz), %dms, Detune: %s, Vol: %.0f%%\\n",
               primaryChime + 1, CHIME_FREQUENCIES[primaryChime], 
               strikeDuration, shouldDetune ? "Yes" : "No", volumeMultiplier * 100);

  // Strike primary chime
  strikeChime(primaryChime, strikeDuration, volumeMultiplier);

  // Add harmonic chimes based on space weather condition
  if (spaceWeatherCondition == "moderate" || spaceWeatherCondition == "storm") {
    delay(100);
    strikeChime((primaryChime + 1) % 3, strikeDuration * 0.7, volumeMultiplier * 0.6);
  }

  if (spaceWeatherCondition == "storm" || spaceWeatherCondition == "extreme") {
    delay(150);
    strikeChime((primaryChime + 2) % 3, strikeDuration * 0.5, volumeMultiplier * 0.4);
  }

  // Detune effect with rapid strikes for Bz activity
  if (shouldDetune) {
    delay(200);
    for (int i = 0; i < 3; i++) {
      strikeChime(primaryChime, 30, volumeMultiplier * 0.3);
      delay(80);
    }
  }
}

void strikeChime(int chimeIndex, int duration, float volume) {
  if (chimeIndex < 0 || chimeIndex > 2) return;

  int pin = CHIME_PINS[chimeIndex];
  int pulseDuration = (int)(duration * volume);

  digitalWrite(pin, HIGH);
  delay(pulseDuration);
  digitalWrite(pin, LOW);
}

void playStartupChime() {
  Serial.println("Playing startup chime sequence");

  // Ascending arpeggio: E3 -> A3 -> C#4
  for (int i = 0; i < 3; i++) {
    strikeChime(i, 60, 0.8);
    delay(300);
  }

  delay(500);

  // Final chord (all three)
  for (int i = 0; i < 3; i++) {
    digitalWrite(CHIME_PINS[i], HIGH);
  }
  delay(80);
  for (int i = 0; i < 3; i++) {
    digitalWrite(CHIME_PINS[i], LOW);
  }
}

void playStormPartyChime() {
  // Dramatic storm party activation sound
  for (int i = 0; i < 5; i++) {
    strikeChime(2, 40, 1.0); // C#4 rapid strikes
    delay(60);
  }
  delay(200);
  strikeChime(0, 100, 1.0); // Deep E3
}

void updateHeartbeat() {
  static unsigned long lastBlink = 0;
  static bool ledState = false;

  if (millis() - lastBlink > 1000) {
    ledState = !ledState;
    digitalWrite(PIN_HEARTBEAT, ledState);
    lastBlink = millis();
  }
}

void logPowerTelemetry() {
  if (!SOLAR_HARVEST_LOG) return;

  float busVoltage = ina219.getBusVoltage_V();
  float current = ina219.getCurrent_mA();
  float power = ina219.getPower_mW();

  Serial.printf("Power: %.2fV, %.1fmA, %.1fmW\\n", busVoltage, current, power);

  // TODO: Send telemetry via MQTT or HTTP POST for monitoring
}

void printStatus() {
  Serial.println("\\n=== Solar Lighthouse Status ===");
  Serial.printf("Firmware: %s\\n", firmwareVersion);
  Serial.printf("WiFi: %s\\n", WiFi.status() == WL_CONNECTED ? "Connected" : "Disconnected");
  Serial.printf("Space Weather: %s\\n", spaceWeatherCondition.c_str());
  Serial.printf("Night Mode: %s\\n", nightModeActive ? "Active" : "Inactive");
  Serial.printf("Storm Party: %s\\n", stormPartyMode ? "Active" : "Inactive");
  Serial.printf("Solar Wind: %.1f km/s, %.1f p/cm³, %.1f nT\\n", 
               currentVelocity, currentDensity, currentBz);
  Serial.println("================================\\n");
}
`;
}

