import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { playChord } from "@/lib/audio-engine";

interface ChordVisualizationProps {
  velocity: number;
  density: number;
  bz: number;
}

export function ChordVisualization({ velocity, density, bz }: ChordVisualizationProps) {
  const [isPlaying, setIsPlaying] = useState(false);
  const { toast } = useToast();

  // Calculate chord data
  const { data: chordData, isLoading } = useQuery({
    queryKey: ["/api/mapping/calculate-chord", { velocity, density, bz }],
    queryFn: async () => {
      const response = await fetch("/api/mapping/calculate-chord", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ velocity, density, bz })
      });
      if (!response.ok) throw new Error("Failed to calculate chord");
      return response.json();
    },
    enabled: !!(velocity && density && bz !== undefined)
  });

  const handlePlayChord = async () => {
    if (!chordData) return;

    try {
      setIsPlaying(true);
      await playChord(chordData);
      toast({
        title: "Chord Played",
        description: `${chordData.baseNote} chord with ${chordData.condition} space weather characteristics`,
      });
    } catch (error) {
      toast({
        title: "Audio Error",
        description: "Failed to play chord. Check audio permissions.",
        variant: "destructive",
      });
    } finally {
      setIsPlaying(false);
    }
  };

  if (isLoading) {
    return (
      <section className="mb-8">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-6">
              <Skeleton className="h-8 w-64" />
              <Skeleton className="h-10 w-32" />
            </div>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div className="space-y-4">
                <Skeleton className="h-6 w-48" />
                <Skeleton className="h-64 w-full" />
              </div>
              <div className="space-y-4">
                <Skeleton className="h-6 w-48" />
                <div className="space-y-3">
                  {[1, 2, 3].map(i => (
                    <Skeleton key={i} className="h-20 w-full" />
                  ))}
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </section>
    );
  }

  if (!chordData) return null;

  const getConditionColor = (condition: string) => {
    switch (condition) {
      case 'quiet': return 'text-accent';
      case 'moderate': return 'text-warning';
      case 'storm': return 'text-destructive';
      default: return 'text-muted-foreground';
    }
  };

  const getConditionBg = (condition: string) => {
    switch (condition) {
      case 'quiet': return 'bg-accent/10 border-accent/30';
      case 'moderate': return 'bg-warning/10 border-warning/30';
      case 'storm': return 'bg-destructive/10 border-destructive/30';
      default: return 'bg-secondary/30 border-border';
    }
  };

  return (
    <section className="mb-8">
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold">Current Solar Wind Chord</h2>
            <Button 
              onClick={handlePlayChord}
              disabled={isPlaying}
              className="bg-primary text-primary-foreground hover:bg-primary/90"
              data-testid="button-play-chord"
            >
              <i className={`fas ${isPlaying ? 'fa-spinner fa-spin' : 'fa-play'} mr-2`} />
              {isPlaying ? "Playing..." : "Play Chord"}
            </Button>
          </div>

          {/* Current Condition Badge */}
          <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium mb-6 ${getConditionBg(chordData.condition)}`}>
            <div className={`w-2 h-2 rounded-full mr-2 ${getConditionColor(chordData.condition).replace('text-', 'bg-')}`} />
            <span className={getConditionColor(chordData.condition)}>
              {chordData.condition.charAt(0).toUpperCase() + chordData.condition.slice(1)} Space Weather
            </span>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Waveform Display */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Waveform Analysis</h3>
              <div className="bg-secondary/30 rounded-lg p-4 h-64 flex items-end justify-center space-x-1">
                {/* Generate visual waveform based on chord data */}
                {Array.from({ length: 32 }, (_, i) => {
                  const baseHeight = 20 + (Math.sin(i * 0.3) * 15);
                  const velocityMod = (chordData.midiNote - 36) / 48 * 30;
                  const densityMod = chordData.decayTime * 10;
                  const bzMod = chordData.detuneCents !== 0 ? Math.sin(i * 0.8) * 10 : 0;

                  const height = Math.max(10, baseHeight + velocityMod + densityMod + bzMod);

                  let colorClass = 'bg-primary';
                  if (i > 20) colorClass = 'bg-accent';
                  if (i > 26) colorClass = chordData.detuneCents !== 0 ? 'bg-warning' : 'bg-accent';

                  return (
                    <div 
                      key={i}
                      className={`w-2 ${colorClass} rounded-t transition-all duration-300`}
                      style={{ height: `${height}%` }}
                    />
                  );
                })}
              </div>
              <div className="text-sm text-muted-foreground">
                Live chord visualization showing harmonics and beat frequencies
              </div>
            </div>

            {/* Chord Components */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Chord Components</h3>
              <div className="space-y-3">
                <div className="bg-secondary/30 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium">Base Note (Velocity)</span>
                    <span className="text-primary font-mono" data-testid="text-chord-base-note">
                      {chordData.baseNote} ({chordData.frequency.toFixed(1)} Hz)
                    </span>
                  </div>
                  <div className="w-full bg-secondary rounded-full h-2">
                    <div 
                      className="bg-primary h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${((chordData.midiNote - 36) / 48) * 100}%` }}
                    />
                  </div>
                </div>

                <div className="bg-secondary/30 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium">Envelope (Density)</span>
                    <span className="text-accent font-mono" data-testid="text-chord-envelope">
                      {chordData.decayTime}s decay
                    </span>
                  </div>
                  <div className="w-full bg-secondary rounded-full h-2">
                    <div 
                      className="bg-accent h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${(chordData.decayTime / 5.0) * 100}%` }}
                    />
                  </div>
                </div>

                <div className="bg-secondary/30 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium">Detune (Bz Field)</span>
                    <span className={`font-mono ${chordData.detuneCents !== 0 ? 'text-warning' : 'text-muted-foreground'}`} data-testid="text-chord-detune">
                      {chordData.detuneCents} cents
                    </span>
                  </div>
                  <div className="w-full bg-secondary rounded-full h-2">
                    <div 
                      className={`h-2 rounded-full transition-all duration-300 ${chordData.detuneCents !== 0 ? 'bg-warning' : 'bg-muted-foreground/30'}`}
                      style={{ width: `${chordData.detuneCents !== 0 ? 80 : 5}%` }}
                    />
                  </div>
                </div>
              </div>

              {/* Chord Summary */}
              <div className="mt-6 p-4 bg-muted/30 rounded-lg">
                <h4 className="font-medium mb-2">Musical Interpretation</h4>
                <div className="text-sm text-muted-foreground space-y-1">
                  <div>
                    <span className="font-medium">Pitch:</span> {chordData.baseNote} reflects solar wind speed of {velocity.toFixed(1)} km/s
                  </div>
                  <div>
                    <span className="font-medium">Timbre:</span> {chordData.decayTime < 1 ? 'Sharp pluck' : chordData.decayTime > 3 ? 'Long gong' : 'Medium bell'} from density {density.toFixed(1)} p/cm³
                  </div>
                  <div>
                    <span className="font-medium">Harmony:</span> {chordData.detuneCents !== 0 ? 'Discordant beating warns of geomagnetic activity' : 'Clean tone indicates stable magnetic field'}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </section>
  );
}

client/src/components/data-dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";

export function DataDashboard() {
  // Fetch solar wind history for charts
  const { data: historyData, isLoading: historyLoading } = useQuery({
    queryKey: ["/api/solar-wind/history"],
    queryFn: async ({ queryKey }) => {
      const response = await fetch(queryKey.join("/") + "?limit=24");
      if (!response.ok) throw new Error("Failed to fetch history data");
      return response.json();
    }
  });

  // Fetch system status for connection info
  const { data: systemStatus } = useQuery({
    queryKey: ["/api/system/status"]
  });

  const dataStreamStatus = systemStatus?.find(s => s.component === 'data_stream');
  const networkStatus = systemStatus?.find(s => s.component === 'network');

  const formatTimestamp = (timestamp: string) => {
    return new Date(timestamp).toLocaleString('en-US', {
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: false
    });
  };

  const getParameterTrend = (data: any[], parameter: string) => {
    if (!data || data.length < 2) return 'stable';

    const recent = data.slice(-3);
    const values = recent.map(d => d[parameter]);
    const trend = values[values.length - 1] - values[0];

    if (Math.abs(trend) < (values[0] * 0.1)) return 'stable';
    return trend > 0 ? 'rising' : 'falling';
  };

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case 'rising': return 'fas fa-arrow-up text-warning';
      case 'falling': return 'fas fa-arrow-down text-accent';
      default: return 'fas fa-minus text-muted-foreground';
    }
  };

  const generateSparklineData = (data: any[], parameter: string) => {
    if (!data || data.length === 0) return [];

    const values = data.slice(-12).map(d => d[parameter]);
    const max = Math.max(...values);
    const min = Math.min(...values);
    const range = max - min || 1;

    return values.map(value => ({
      value,
      normalized: ((value - min) / range) * 100
    }));
  };

  if (historyLoading) {
    return (
      <section className="mb-8">
        <h2 className="text-2xl font-bold mb-6">Real-time Data Dashboard</h2>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {[1, 2].map(i => (
            <Card key={i}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <Skeleton className="h-6 w-48" />
                  <Skeleton className="h-5 w-20" />
                </div>
                <Skeleton className="h-48 w-full mb-4" />
                <div className="space-y-2">
                  <Skeleton className="h-4 w-full" />
                  <Skeleton className="h-4 w-3/4" />
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </section>
    );
  }

  const latestReading = historyData?.[0];

  return (
    <section className="mb-8">
      <h2 className="text-2xl font-bold mb-6">Real-time Data Dashboard</h2>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

        {/* NOAA DSCOVR Data Stream */}
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">NOAA DSCOVR Data Stream</h3>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${dataStreamStatus?.status === 'active' ? 'bg-accent pulse-animation' : 'bg-destructive'}`} />
                <span className="text-sm" data-testid="text-data-connection-status">
                  {dataStreamStatus?.status === 'active' ? 'Connected' : 'Disconnected'}
                </span>
              </div>
            </div>

            <div className="space-y-4">
              {latestReading ? (
                <div className="bg-secondary/30 rounded-lg p-4 font-mono text-sm">
                  <div className="text-muted-foreground mb-2">
                    Last Update: <span data-testid="text-last-update">{formatTimestamp(latestReading.timestamp)}</span>
                  </div>
                  <div className="space-y-1">
                    <div>
                      <span className="text-primary">velocity:</span> <span data-testid="text-raw-velocity">{latestReading.velocity.toFixed(1)}</span> km/s
                    </div>
                    <div>
                      <span className="text-accent">density:</span> <span data-testid="text-raw-density">{latestReading.density.toFixed(2)}</span> p/cm³
                    </div>
                    <div>
                      <span className="text-warning">bz_gsm:</span> <span data-testid="text-raw-bz">{latestReading.bz.toFixed(1)}</span> nT
                    </div>
                    {latestReading.bt && (
                      <div>
                        <span className="text-muted-foreground">bt:</span> <span data-testid="text-raw-bt">{latestReading.bt.toFixed(1)}</span> nT
                      </div>
                    )}
                    {latestReading.temperature && (
                      <div>
                        <span className="text-muted-foreground">temperature:</span> <span data-testid="text-raw-temperature">{Math.round(latestReading.temperature)}</span> K
                      </div>
                    )}
                  </div>
                </div>
              ) : (
                <div className="bg-secondary/30 rounded-lg p-4 text-center text-muted-foreground">
                  <i className="fas fa-database text-2xl mb-2" />
                  <div>No recent data available</div>
                </div>
              )}

              <div className="text-sm text-muted-foreground space-y-1">
                <div className="flex items-center justify-between">
                  <span>Update Frequency:</span>
                  <span className="text-accent">60 seconds</span>
                </div>
                <div className="flex items-center justify-between">
                  <span>Data Source:</span>
                  <span className="text-primary">NOAA SWPC</span>
                </div>
                <div className="flex items-center justify-between">
                  <span>API Endpoint:</span>
                  <span className="text-warning font-mono text-xs">real-time-solar-wind.json</span>
                </div>
                <div className="flex items-center justify-between">
                  <span>Network Status:</span>
                  <Badge variant={networkStatus?.status === 'active' ? 'default' : 'destructive'}>
                    {networkStatus?.status === 'active' ? 'Online' : 'Offline'}
                  </Badge>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Parameter History Charts */}
        <Card>
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold mb-4">Parameter History (24h)</h3>
            <div className="space-y-6">

              {/* Velocity Trend */}
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-primary">Solar Wind Velocity</span>
                  <div className="flex items-center space-x-2">
                    <i className={getTrendIcon(getParameterTrend(historyData, 'velocity'))} />
                    <span className="text-xs text-muted-foreground">
                      {getParameterTrend(historyData, 'velocity')}
                    </span>
                  </div>
                </div>
                <div className="h-12 bg-secondary/30 rounded-lg p-2 flex items-end justify-center">
                  {historyData && historyData.length > 0 ? (
                    <div className="w-full h-full flex items-end justify-between space-x-1">
                      {generateSparklineData(historyData, 'velocity').map((point, i) => (
                        <div 
                          key={i}
                          className="flex-1 bg-primary rounded-t transition-all duration-300"
                          style={{ height: `${Math.max(10, point.normalized)}%` }}
                          title={`${point.value.toFixed(1)} km/s`}
                        />
                      ))}
                    </div>
                  ) : (
                    <div className="text-xs text-muted-foreground">No data available</div>
                  )}
                </div>
                <div className="text-xs text-muted-foreground">
                  Range: {historyData && historyData.length > 0 ? 
                    `${Math.min(...historyData.map(d => d.velocity)).toFixed(0)} - ${Math.max(...historyData.map(d => d.velocity)).toFixed(0)} km/s` 
                    : 'N/A'}
                </div>
              </div>

              {/* Density Trend */}
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-accent">Plasma Density</span>
                  <div className="flex items-center space-x-2">
                    <i className={getTrendIcon(getParameterTrend(historyData, 'density'))} />
                    <span className="text-xs text-muted-foreground">
                      {getParameterTrend(historyData, 'density')}
                    </span>
                  </div>
                </div>
                <div className="h-12 bg-secondary/30 rounded-lg p-2 flex items-end justify-center">
                  {historyData && historyData.length > 0 ? (
                    <div className="w-full h-full flex items-end justify-between space-x-1">
                      {generateSparklineData(historyData, 'density').map((point, i) => (
                        <div 
                          key={i}
                          className="flex-1 bg-accent rounded-t transition-all duration-300"
                          style={{ height: `${Math.max(10, point.normalized)}%` }}
                          title={`${point.value.toFixed(2)} p/cm³`}
                        />
                      ))}
                    </div>
                  ) : (
                    <div className="text-xs text-muted-foreground">No data available</div>
                  )}
                </div>
                <div className="text-xs text-muted-foreground">
                  Range: {historyData && historyData.length > 0 ? 
                    `${Math.min(...historyData.map(d => d.density)).toFixed(1)} - ${Math.max(...historyData.map(d => d.density)).toFixed(1)} p/cm³` 
                    : 'N/A'}
                </div>
              </div>

              {/* Bz Trend */}
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-warning">Bz Magnetic Field</span>
                  <div className="flex items-center space-x-2">
                    <i className={getTrendIcon(getParameterTrend(historyData, 'bz'))} />
                    <span className="text-xs text-muted-foreground">
                      {getParameterTrend(historyData, 'bz')}
                    </span>
                  </div>
                </div>
                <div className="h-12 bg-secondary/30 rounded-lg p-2 flex items-end justify-center relative">
                  {historyData && historyData.length > 0 ? (
                    <>
                      {/* Zero line indicator */}
                      <div className="absolute inset-0 flex items-center">
                        <div className="w-full h-px bg-muted-foreground/30 border-dashed border-t"></div>
                      </div>
                      <div className="w-full h-full flex items-end justify-between space-x-1 relative">
                        {generateSparklineData(historyData, 'bz').map((point, i) => {
                          const originalValue = historyData[historyData.length - 12 + i]?.bz || 0;
                          const isNegative = originalValue < 0;
                          return (
                            <div 
                              key={i}
                              className={`flex-1 rounded-t transition-all duration-300 ${
                                isNegative ? 'bg-destructive' : 'bg-warning'
                              }`}
                              style={{ height: `${Math.max(5, point.normalized)}%` }}
                              title={`${originalValue.toFixed(1)} nT`}
                            />
                          );
                        })}
                      </div>
                    </>
                  ) : (
                    <div className="text-xs text-muted-foreground">No data available</div>
                  )}
                </div>
                <div className="text-xs text-muted-foreground">
                  Range: {historyData && historyData.length > 0 ? 
                    `${Math.min(...historyData.map(d => d.bz)).toFixed(1)} - ${Math.max(...historyData.map(d => d.bz)).toFixed(1)} nT` 
                    : 'N/A'}
                </div>
              </div>
            </div>

            <div className="mt-4 pt-4 border-t border-border text-sm text-muted-foreground">
              Historical trends help identify space weather patterns and forecast upcoming chord changes
            </div>
          </CardContent>
        </Card>
      </div>
    </section>
  );
}

client/src/components/hardware-config.ts.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export function HardwareConfig() {
  const [firmwareCode, setFirmwareCode] = useState("");
  const [showFirmware, setShowFirmware] = useState(false);
  const [selectedConfigId, setSelectedConfigId] = useState<string>("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch hardware configurations
  const { data: hardwareConfigs = [], isLoading } = useQuery({
    queryKey: ["/api/hardware/configs"]
  });

  // Fetch system status
  const { data: systemStatus = [] } = useQuery({
    queryKey: ["/api/system/status"]
  });

  // Generate firmware mutation
  const generateFirmwareMutation = useMutation({
    mutationFn: async (configId: string) => {
      const response = await apiRequest("POST", "/api/hardware/generate-firmware", { configId });
      return response.json();
    },
    onSuccess: (data) => {
      setFirmwareCode(data.firmware_code);
      setShowFirmware(true);
      toast({
        title: "Firmware Generated",
        description: "ESP32 firmware code has been generated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Generation Failed",
        description: error.message || "Failed to generate firmware code",
        variant: "destructive",
      });
    }
  });

  // Test chimes mutation
  const testChimesMutation = useMutation({
    mutationFn: () => apiRequest("POST", "/api/system/test-chimes"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/system/status"] });
      toast({
        title: "Chime Test Started",
        description: "Testing all chimes - check hardware for audio feedback",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Test Failed",
        description: error.message || "Failed to start chime test",
        variant: "destructive",
      });
    }
  });

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(firmwareCode);
      toast({
        title: "Copied to Clipboard",
        description: "Firmware code has been copied to your clipboard",
      });
    } catch (error) {
      toast({
        title: "Copy Failed",
        description: "Unable to copy to clipboard",
        variant: "destructive",
      });
    }
  };

  const downloadFirmware = () => {
    const blob = new Blob([firmwareCode], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'solar_wind_chime.ino';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    toast({
      title: "Download Started",
      description: "Firmware file (solar_wind_chime.ino) has been downloaded",
    });
  };

  if (isLoading) {
    return (
      <section className="mb-8">
        <h2 className="text-2xl font-bold mb-6">Hardware Configuration</h2>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {[1, 2].map(i => (
            <Card key={i}>
              <CardContent className="p-6">
                <Skeleton className="h-6 w-48 mb-4" />
                <div className="space-y-3">
                  <Skeleton className="h-20 w-full" />
                  <Skeleton className="h-16 w-full" />
                  <Skeleton className="h-10 w-full" />
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </section>
    );
  }

  // Initialize selected config when configs are loaded
  if (hardwareConfigs?.length && !selectedConfigId) {
    setSelectedConfigId(hardwareConfigs[0].id);
  }

  const selectedConfig = hardwareConfigs?.find(config => config.id === selectedConfigId) || hardwareConfigs?.[0];
  const chimeStatus = systemStatus?.find(s => s.component === 'chimes');
  const networkStatus = systemStatus?.find(s => s.component === 'network');

  return (
    <section className="mb-8">
      <h2 className="text-2xl font-bold mb-6">Hardware Configuration</h2>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

        {/* ESP32 Configuration */}
        <Card>
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold mb-4 flex items-center">
              <i className="fas fa-microchip mr-2 text-primary" />
              Hardware Configuration
            </h3>

            {/* Device Selection */}
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Select Device:</label>
              <Select value={selectedConfigId} onValueChange={setSelectedConfigId}>
                <SelectTrigger data-testid="select-hardware-device">
                  <SelectValue placeholder="Choose hardware configuration" />
                </SelectTrigger>
                <SelectContent>
                  {hardwareConfigs?.map(config => (
                    <SelectItem key={config.id} value={config.id}>
                      {config.device_name}
                      {config.device_name === "Solar Lighthouse Prototype" && (
                        <Badge variant="secondary" className="ml-2">New</Badge>
                      )}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {selectedConfig ? (
              <div className="space-y-4">
                <div className="bg-secondary/30 rounded-lg p-4">
                  <div className="text-sm font-medium mb-2">Pin Configuration</div>
                  <div className="font-mono text-sm space-y-1">
                    <div>GPIO {selectedConfig.pin_velocity}: <span className="text-primary">Velocity Chime</span></div>
                    <div>GPIO {selectedConfig.pin_density}: <span className="text-accent">Density Chime</span></div>
                    <div>GPIO {selectedConfig.pin_bz}: <span className="text-warning">Bz Chime</span></div>
                    <div>GPIO {selectedConfig.pin_status_led}: <span className="text-muted-foreground">Status LED</span></div>
                    <div>GPIO {selectedConfig.pin_sda}: <span className="text-muted-foreground">SDA (I2C)</span></div>
                    <div>GPIO {selectedConfig.pin_scl}: <span className="text-muted-foreground">SCL (I2C)</span></div>
                  </div>
                </div>

                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <span className="text-sm">WiFi Status:</span>
                    <Badge variant={networkStatus?.status === 'active' ? 'default' : 'destructive'}>
                      {networkStatus?.status === 'active' ? 'Connected' : 'Disconnected'}
                    </Badge>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Firmware Version:</span>
                    <span className="text-muted-foreground font-mono">{selectedConfig.firmware_version}</span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Update Interval:</span>
                    <span className="text-warning">{selectedConfig.update_interval}s</span>
                  </div>
                </div>

                <Button 
                  className="w-full bg-primary text-primary-foreground hover:bg-primary/90"
                  onClick={() => generateFirmwareMutation.mutate(selectedConfigId)}
                  disabled={generateFirmwareMutation.isPending}
                  data-testid="button-generate-firmware"
                >
                  <i className={`fas ${generateFirmwareMutation.isPending ? 'fa-spinner fa-spin' : 'fa-download'} mr-2`} />
                  {generateFirmwareMutation.isPending ? "Generating..." : "Generate Firmware Code"}
                </Button>
              </div>
            ) : (
              <div className="text-center text-muted-foreground">
                <i className="fas fa-exclamation-triangle text-2xl mb-2" />
                <p>No hardware configuration found</p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Chime Hardware Setup */}
        <Card>
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold mb-4 flex items-center">
              <i className="fas fa-bell mr-2 text-accent" />
              Chime Hardware Setup
            </h3>

            <div className="space-y-4">
              <div className="bg-secondary/30 rounded-lg p-4">
                <div className="text-sm font-medium mb-2">Component List</div>
                <div className="text-sm space-y-1">
                  <div className="flex justify-between">
                    <span>Magnetic Door Chimes</span>
                    <span className="text-primary">3x 5V</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Aluminum Tubes</span>
                    <span className="text-accent">3x Different Lengths</span>
                  </div>
                  <div className="flex justify-between">
                    <span>18650 Li-ion Battery</span>
                    <span className="text-warning">3.7V 3000mAh</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Solar Panel</span>
                    <span className="text-muted-foreground">6V 1W</span>
                  </div>
                </div>
              </div>

              <div className="bg-secondary/30 rounded-lg p-4 h-32 flex items-center justify-center">
                <div className="text-center text-muted-foreground">
                  <i className="fas fa-drafting-compass text-3xl mb-2" />
                  <div className="text-sm">Hardware Assembly Diagram</div>
                  <div className="text-xs">ESP32 → Solenoids → Chimes</div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-2">
                <Button 
                  variant="outline"
                  onClick={() => testChimesMutation.mutate()}
                  disabled={testChimesMutation.isPending}
                  data-testid="button-test-chimes"
                >
                  <i className={`fas ${testChimesMutation.isPending ? 'fa-spinner fa-spin' : 'fa-play-circle'} mr-2`} />
                  Test Chimes
                </Button>

                <Button variant="outline" data-testid="button-download-guide">
                  <i className="fas fa-file-download mr-2" />
                  Assembly Guide
                </Button>
              </div>

              {chimeStatus && (
                <div className="text-xs text-muted-foreground bg-muted bg-opacity-20 p-2 rounded">
                  <strong>Status:</strong> {chimeStatus.details}
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Firmware Code Dialog */}
      <Dialog open={showFirmware} onOpenChange={setShowFirmware}>
        <DialogContent className="max-w-4xl max-h-[80vh]">
          <DialogHeader>
            <DialogTitle>Generated ESP32 Firmware Code</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div className="flex space-x-2">
              <Button onClick={copyToClipboard} variant="outline" size="sm" data-testid="button-copy-firmware">
                <i className="fas fa-copy mr-2" />
                Copy to Clipboard
              </Button>
              <Button onClick={downloadFirmware} variant="outline" size="sm" data-testid="button-download-firmware">
                <i className="fas fa-download mr-2" />
                Download .ino File
              </Button>
            </div>
            <Textarea
              value={firmwareCode}
              readOnly
              className="font-mono text-xs h-96 resize-none"
              placeholder="Generated firmware code will appear here..."
            />
            <div className="text-xs text-muted-foreground">
              <p><strong>Installation Instructions:</strong></p>
              <ol className="list-decimal list-inside space-y-1 mt-1">
                <li>Install Arduino IDE and ESP32 board support</li>
                <li>Install required libraries: ArduinoJson, WiFi</li>
                <li>Copy this code to a new Arduino sketch</li>
                <li>Update WiFi credentials and upload to ESP32</li>
                <li>Connect hardware according to pin configuration</li>
              </ol>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </section>
  );
}

client/src/components/mapping-algorithm..content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const mappingFormSchema = z.object({
  name: z.string().min(1, "Name is required"),
  velocity_min: z.number().min(100).max(300),
  velocity_max: z.number().min(600).max(1000),
  midi_note_min: z.number().min(12).max(60),
  midi_note_max: z.number().min(60).max(108),
  density_min: z.number().min(0.1).max(5),
  density_max: z.number().min(10).max(100),
  decay_time_min: z.number().min(0.1).max(1),
  decay_time_max: z.number().min(2).max(10),
  bz_detune_cents: z.number().min(-50).max(50),
  bz_threshold: z.number().min(-20).max(0)
});

type MappingFormData = z.infer<typeof mappingFormSchema>;

export function MappingAlgorithm() {
  const [showEditor, setShowEditor] = useState(false);
  const [testingCondition, setTestingCondition] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch active mapping configuration
  const { data: activeMapping, isLoading } = useQuery({
    queryKey: ["/api/mapping/active"]
  });

  // Test condition mutation
  const testConditionMutation = useMutation({
    mutationFn: async (condition: string) => {
      const response = await apiRequest("POST", "/api/mapping/test-condition", { condition });
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: `${data.condition.charAt(0).toUpperCase() + data.condition.slice(1)} Condition Tested`,
        description: `Generated ${data.chord.baseNote} chord with ${data.chord.decayTime}s decay`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Test Failed",
        description: error.message || "Failed to test space weather condition",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setTestingCondition(null);
    }
  });

  // Update mapping mutation
  const updateMappingMutation = useMutation({
    mutationFn: async (data: MappingFormData) => {
      if (!activeMapping) throw new Error("No active mapping found");

      const response = await apiRequest("PATCH", `/api/mapping/configs/${activeMapping.id}`, {
        ...data,
        is_active: "true"
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/mapping/active"] });
      setShowEditor(false);
      toast({
        title: "Mapping Updated",
        description: "Solar wind to MIDI mapping configuration has been updated",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update mapping configuration",
        variant: "destructive",
      });
    }
  });

  const form = useForm<MappingFormData>({
    resolver: zodResolver(mappingFormSchema),
    defaultValues: activeMapping ? {
      name: activeMapping.name,
      velocity_min: activeMapping.velocity_min,
      velocity_max: activeMapping.velocity_max,
      midi_note_min: activeMapping.midi_note_min,
      midi_note_max: activeMapping.midi_note_max,
      density_min: activeMapping.density_min,
      density_max: activeMapping.density_max,
      decay_time_min: activeMapping.decay_time_min,
      decay_time_max: activeMapping.decay_time_max,
      bz_detune_cents: activeMapping.bz_detune_cents,
      bz_threshold: activeMapping.bz_threshold
    } : {
      name: "Custom Mapping",
      velocity_min: 200,
      velocity_max: 800,
      midi_note_min: 36,
      midi_note_max: 84,
      density_min: 0.5,
      density_max: 50.0,
      decay_time_min: 0.2,
      decay_time_max: 5.0,
      bz_detune_cents: -20,
      bz_threshold: -5.0
    }
  });

  const handleTestCondition = (condition: string) => {
    setTestingCondition(condition);
    testConditionMutation.mutate(condition);
  };

  const onSubmit = (data: MappingFormData) => {
    updateMappingMutation.mutate(data);
  };

  if (isLoading) {
    return (
      <section className="mb-8">
        <Card>
          <CardContent className="p-6">
            <Skeleton className="h-8 w-64 mb-6" />
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div className="space-y-4">
                <Skeleton className="h-6 w-48" />
                <Skeleton className="h-48 w-full" />
                <Skeleton className="h-10 w-full" />
              </div>
              <div className="space-y-4">
                <Skeleton className="h-6 w-48" />
                <div className="space-y-3">
                  {[1, 2, 3, 4].map(i => (
                    <Skeleton key={i} className="h-16 w-full" />
                  ))}
                </div>
              </div>
            </div>
            <div className="mt-6 pt-6 border-t border-border">
              <Skeleton className="h-6 w-48 mb-4" />
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {[1, 2, 3].map(i => (
                  <Skeleton key={i} className="h-20 w-full" />
                ))}
              </div>
            </div>
          </CardContent>
        </Card>
      </section>
    );
  }

  if (!activeMapping) {
    return (
      <section className="mb-8">
        <Card>
          <CardContent className="p-6 text-center">
            <div className="text-muted-foreground mb-4">
              <i className="fas fa-exclamation-triangle text-4xl mb-2" />
              <h3 className="text-lg font-semibold">No Active Mapping Configuration</h3>
              <p className="text-sm">Unable to load the solar wind to MIDI mapping configuration</p>
            </div>
          </CardContent>
        </Card>
      </section>
    );
  }

  const configDate = new Date(activeMapping.created_at).toISOString().split('T')[0];

  return (
    <section className="mb-8">
      <Card>
        <CardContent className="p-6">
          <h2 className="text-2xl font-bold mb-6">Solar Wind to MIDI Mapping Algorithm</h2>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Lookup Table */}
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">Lookup Table Configuration</h3>
                <Badge variant="outline">Active</Badge>
              </div>
              <div className="bg-secondary/30 rounded-lg p-4 font-mono text-sm">
                <div className="text-accent mb-2">// Solar Wind to MIDI Mapping Table</div>
                <div className="text-muted-foreground mb-2">// Date-stamped: {configDate}</div>
                <div className="space-y-1">
                  <div><span className="text-primary">velocity_range:</span> [{activeMapping.velocity_min}, {activeMapping.velocity_max}] <span className="text-muted-foreground">// km/s</span></div>
                  <div><span className="text-primary">midi_range:</span> [{activeMapping.midi_note_min}, {activeMapping.midi_note_max}] <span className="text-muted-foreground">// C2 to C6</span></div>
                  <div><span className="text-accent">density_min:</span> {activeMapping.density_min} <span className="text-muted-foreground">// p/cm³</span></div>
                  <div><span className="text-accent">density_max:</span> {activeMapping.density_max}</div>
                  <div><span className="text-accent">decay_range:</span> [{activeMapping.decay_time_min}, {activeMapping.decay_time_max}] <span className="text-muted-foreground">// seconds</span></div>
                  <div><span className="text-warning">bz_detune:</span> {activeMapping.bz_detune_cents} <span className="text-muted-foreground">// cents when south</span></div>
                  <div><span className="text-warning">beat_threshold:</span> {activeMapping.bz_threshold} <span className="text-muted-foreground">// nT</span></div>
                </div>
              </div>

              <Dialog open={showEditor} onOpenChange={setShowEditor}>
                <DialogTrigger asChild>
                  <Button 
                    variant="secondary" 
                    className="w-full"
                    data-testid="button-edit-mapping"
                  >
                    <i className="fas fa-edit mr-2" />
                    Edit Mapping Parameters
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-2xl">
                  <DialogHeader>
                    <DialogTitle>Edit Mapping Configuration</DialogTitle>
                  </DialogHeader>
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="name"
                          render={({ field }) => (
                            <FormItem className="col-span-2">
                              <FormLabel>Configuration Name</FormLabel>
                              <FormControl>
                                <Input {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="velocity_min"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Min Velocity (km/s)</FormLabel>
                              <FormControl>
                                <Input type="number" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="velocity_max"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Max Velocity (km/s)</FormLabel>
                              <FormControl>
                                <Input type="number" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="midi_note_min"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Min MIDI Note</FormLabel>
                              <FormControl>
                                <Input type="number" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="midi_note_max"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Max MIDI Note</FormLabel>
                              <FormControl>
                                <Input type="number" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="bz_detune_cents"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Bz Detune (cents)</FormLabel>
                              <FormControl>
                                <Input type="number" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="bz_threshold"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Bz Threshold (nT)</FormLabel>
                              <FormControl>
                                <Input type="number" step="0.1" {...field} onChange={e => field.onChange(Number(e.target.value))} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="flex space-x-2 pt-4">
                        <Button 
                          type="submit" 
                          disabled={updateMappingMutation.isPending}
                          data-testid="button-save-mapping"
